        mHdltvoutHandle << " , " <<  " \"global_training_set_V_ce0\" :  \"" << global_training_set_V_ce0.read() << "\" ";
        mHdltvinHandle << " , " <<  " \"global_training_set_V_q0\" :  \"" << global_training_set_V_q0.read() << "\" ";
        mHdltvoutHandle << " , " <<  " \"global_test_set_V_address0\" :  \"" << global_test_set_V_address0.read() << "\" ";
